const fetch = require('node-fetch');

const API_URL = 'http://localhost:3000'; // Adjust if running on a different port

async function testTranscription() {
  try {
    // Small 1x1 pixel red dot base64 JPEG
    const base64Image = '';

    console.log('Sending request to /api/translate/transcribe...');
    const response = await fetch(`${API_URL}/api/translate/transcribe`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        image: base64Image,
      }),
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    console.log('Response:', data);
  } catch (error) {
    console.error('Error:', error);
  }
}

testTranscription();
